"use strict";function clockScale(t,e){let n=1,o=t.parentNode,s=getComputedStyle(o),c=o.clientWidth-parseInt(s.paddingRight)-parseInt(s.paddingLeft),a=o.clientHeight-parseInt(s.paddingTop)-parseInt(s.paddingBottom);n=c<=a?c/e:a/e,t.style.transform="scale("+n+")"}function setRotateValue(t,e){t.style.transform="translateX(-50%) rotate("+e+"deg)",t.style.opacity="1"}function setHours(t,e,n){setRotateValue(t,30*e+.5*n)}function setMinutes(t,e,n){setRotateValue(t,6*e+.1*n)}function setSeconds(t,e){setRotateValue(t,6*e)}window.onload=function(){const t=new Date,e=t.getHours(),n=t.getMinutes(),o=t.getSeconds(),s=document.getElementById("clock"),c=s.querySelector(".clock__hand--hours"),a=s.querySelector(".clock__hand--minutes"),l=s.querySelector(".clock__hand--seconds");setHours(c,e,n),setMinutes(a,n,o),setSeconds(l,o),clockScale(s,s.offsetWidth)};